mapper.delete:
DELETE 
FROM 
	tbl_jinlong_system_menu


		type_id = #{typeId}


		AND level = #{level}


		AND parent_id = #{parentId}


		AND menu_code = #{menuCode}


		AND menu_name = #{menuName}


		AND menu_url = #{menuUrl}


		AND state = #{state}

mapper.select:
SELECT
	menu_id   	AS menuId,
	type_id 	AS typeId,
	level		AS level,
	parent_id 	AS parentId,
	menu_code	AS menuCode,
	menu_name 	AS menuName,
	menu_url 	AS menuUrl,
	image		AS image,
	description	AS description,
	state		AS state
FROM 
	tbl_jinlong_system_menu


		type_id = #{typeId}


		AND level = #{level}


		AND parent_id = #{parentId}


		AND menu_code = #{menuCode}


		AND menu_name = #{menuName}


		AND menu_url = #{menuUrl}


		AND state = #{state}


ORDER BY menu_id
