mapper.delete:
DELETE FROM
	tbl_jinlong_system_menu_examine_record

	
		menu_id = #{menuId}
	
	
		AND examine_user_id = #{examineUserId}
	
	
		AND examine_time = #{examineTime}
	
	
		AND is_pass = #{pass}
	
	
		AND state = #{state}

mapper.select:
SELECT 
	examine_id		AS examineId,
	menu_id			AS menuId,
	examine_user_id AS examineUserId,
	examine_time	AS examineTime,
	is_pass			AS pass,
	description		AS description,
	state			AS state
FROM
	tbl_jinlong_system_menu_examine_record

	
		menu_id = #{menuId}
	
	
		AND examine_user_id = #{examineUserId}
	
	
		AND examine_time = #{examineTime}
	
	
		AND is_pass = #{pass}
	
	
		AND state = #{state}
	

ORDER BY examine_id DESC

