mapper.delete:
DELETE 
FROM 
	tbl_jinlong_common_file_info 


		file_type_id = #{fileTypeId}


		AND user_id = #{userId}


		AND init_name = #{initName}


		AND file_name = #{fileName}


		AND submitTime = #{submitTime}

mapper.select:
SELECT file_id AS fileId, file_type_id AS fileTypeId, user_id AS userId, init_name AS initName, file_name AS fileName, file_path AS filePath, submit_time AS submitTime, description AS description, state AS state FROM tbl_jinlong_common_file_info
init_name = #{initName}
AND file_name = #{fileName}

SELECT 
	COUNT(1) 
FROM 
	tbl_jinlong_common_file_info


		file_type_id = #{fileTypeId}


		AND user_id = #{userId}


		AND init_name = #{initName}


		AND file_name = #{fileName}


		AND submitTime = #{submitTime}

SELECT
	file_id 		AS fileId,
	file_type_id 	AS fileTypeId,
	user_id 		AS userId,
	init_name 		AS initName,
	file_name 		AS fileName,
	file_path 		AS filePath,
	submit_time 	AS submitTime,
	description		AS description,
	state			AS state
FROM 
	tbl_jinlong_common_file_info


		file_type_id = #{fileTypeId}


		AND user_id = #{userId}


		AND init_name = #{initName}


		AND file_name = #{fileName}


		AND submitTime = #{submitTime}


ORDER BY file_id DESC

	LIMIT #{startRow} , #{pageSize}
