mapper.delete:
DELETE FROM
	tbl_jinlong_system_role_examine


		examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}

mapper.select:
SELECT role_id AS roleId, examine_user_id AS examineUserId, examine_time AS examineTime, is_pass AS pass, description AS description, state AS state FROM tbl_jinlong_system_role_examine
examine_user_id = #{examineUserId}
AND examine_time = #{examineTime}
AND is_pass = #{pass}
AND state = #{state}
ORDER BY role_id DESC

SELECT 
	COUNT(1)
FROM
	tbl_jinlong_system_role_examine


		examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}


ORDER BY role_id DESC

SELECT 
	role_id			AS roleId,
	examine_user_id AS examineUserId,
	examine_time	AS examineTime,
	is_pass			AS pass,
	description		AS description,
	state			AS state
FROM
	tbl_jinlong_system_role_examine


		examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}


ORDER BY role_id DESC

	LIMIT #{startRow}, #{pageSize}
