mapper.delete:
DELETE 
FROM 
	tbl_jinlong_system_role_menu rm 
	LEFT JOIN tbl_jinlong_system_menu m ON rm.menu_id = m.menu_id 
	LEFT JOIN tbl_jinlong_system_role r ON rm.role_id = r.role_id


		rm.role_id = #{roleId}
		AND r.role_id = #{roleId}


		AND rm.menu_id = #{menuId}
		AND m.menu_id = #{menuId}

mapper.select:
SELECT role_menu_id AS roleMenuId, role_id AS roleId, menu_id AS menuId FROM tbl_jinlong_system_role_menu
role_menu_id = #{id}
ORDER BY role_menu_id

SELECT role_menu_id AS roleMenuId, role_id AS roleId, menu_id AS menuId FROM tbl_jinlong_system_role_menu
role_id = #{roleId}
AND menu_id = #{menuId}

SELECT rm.role_menu_id AS roleMenuId, r.role_id AS roleId, r.role_name AS roleName, r.description AS roleInfo, m.menu_id AS menuId, m.menu_name AS menuName, m.parent_id AS parentId, m.menu_url AS menuUrl, m.image AS image FROM tbl_jinlong_system_role_menu rm LEFT JOIN tbl_jinlong_system_menu m ON rm.menu_id = m.menu_id LEFT JOIN tbl_jinlong_system_role r ON rm.role_id = r.role_id ORDER BY role_menu_id DESC
rm.role_id = #{roleId} AND r.role_id = #{roleId}
AND rm.menu_id = #{menuId} AND m.menu_id = #{menuId}

SELECT 
	COUNT(1) 
FROM 
	tbl_jinlong_system_role_menu rm 
	LEFT JOIN tbl_jinlong_system_menu m ON rm.menu_id = m.menu_id 
	LEFT JOIN tbl_jinlong_system_role r ON rm.role_id = r.role_id


		rm.role_id = #{roleId}
		AND r.role_id = #{roleId}


		AND rm.menu_id = #{menuId}
		AND m.menu_id = #{menuId}

SELECT rm.role_menu_id AS roleMenuId, r.role_id AS roleId, r.role_name AS roleName, r.description AS roleInfo, m.menu_id AS menuId, m.menu_name AS menuName, m.parent_id AS parentId, m.menu_url AS menuUrl, m.image AS image FROM tbl_jinlong_system_role_menu rm LEFT JOIN tbl_jinlong_system_menu m ON rm.menu_id = m.menu_id LEFT JOIN tbl_jinlong_system_role r ON rm.role_id = r.role_id
rm.role_id = #{roleId} AND r.role_id = #{roleId}
AND rm.menu_id = #{menuId} AND m.menu_id = #{menuId}
ORDER BY role_menu_id
LIMIT #{startRow} , #{pageSize}
