mapper.delete:
DELETE 
FROM 
	tbl_jinlong_system_user_base


		role_id = #{roleId}


		AND register_time = #{registerTime}


		AND user_name = #{userName} 


		AND mobile_phone = #{mobilePhone}


		AND email = #{email}


		AND state = #{state}

DELETE b, i FROM tbl_jinlong_system_user_base b LEFT JOIN tbl_jinlong_system_user_info i ON b.user_id = i.user_id
b.user_id IN
#{userId}

mapper.select:
    SELECT
	  	user_id 		AS userId,
	  	role_id 		AS roleId,
	  	affiliated_id	AS affiliatedId,
	  	register_time 	AS registerTime,
	  	user_name 		AS userName,
	  	password		AS password,
	  	mobile_phone 	AS mobilePhone,
	  	email			AS email,
	  	state			AS state
    FROM 
    	tbl_jinlong_system_user_base


	 		user_id = #{userId}


			AND role_id = #{roleId}


			AND register_time = #{registerTime}


			AND user_name = #{userName} 


			AND mobile_phone = #{mobilePhone}


			AND email = #{email}


			AND state = #{state}


    ORDER BY user_id

	SELECT 
		COUNT(1) 
    FROM 
    	tbl_jinlong_system_user_base


	 		user_id = #{userId}


			AND role_id = #{roleId}


			AND register_time = #{registerTime}


			AND user_name = #{userName} 


			AND mobile_phone = #{mobilePhone}


			AND email = #{email}


			AND state = #{state}

    SELECT
	  	user_id 		AS userId,
	  	role_id 		AS roleId,
	  	affiliated_id	AS affiliatedId,
	  	register_time 	AS registerTime,
	  	user_name 		AS userName,
	  	password		AS password,
	  	mobile_phone 	AS mobilePhone,
	  	email			AS email,
	  	state			AS state
    FROM 
    	tbl_jinlong_system_user_base


	 		user_id = #{userId}


			AND role_id = #{roleId}


			AND register_time = #{registerTime}


			AND user_name = #{userName} 


			AND mobile_phone = #{mobilePhone}


			AND email = #{email}


			AND state = #{state}


    ORDER BY user_id DESC

    	LIMIT #{startRow} , #{pageSize}
