mapper.delete:
DELETE FROM
	tbl_jinlong_system_user_examine_record


		user_id = #{userId}


		AND examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}

mapper.select:
SELECT 
	examine_id		AS examineId,
	user_id			AS userId,
	examine_user_id AS examineUserId,
	examine_time	AS examineTime,
	is_pass			AS pass,
	description		AS description,
	state			AS state
FROM
	tbl_jinlong_system_user_examine_record


		user_id = #{userId}


		AND examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}


ORDER BY examine_id DESC

SELECT 
	COUNT(1)
FROM
	tbl_jinlong_system_user_examine_record


		user_id = #{userId}


		AND examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}


ORDER BY examine_id DESC

SELECT 
	examine_id		AS examineId,
	user_id			AS userId,
	examine_user_id AS examineUserId,
	examine_time	AS examineTime,
	is_pass			AS pass,
	description		AS description,
	state			AS state
FROM
	tbl_jinlong_system_user_examine_record


		user_id = #{userId}


		AND examine_user_id = #{examineUserId}


		AND examine_time = #{examineTime}


		AND is_pass = #{pass}


		AND state = #{state}


ORDER BY examine_id DESC

	LIMIT #{startRow}, #{pageSize}
