mapper.delete:
DELETE 
FROM 
	tbl_jinlong_system_user_info


		real_name = #{realName}


		AND nick_name = #{nickName}


		AND id_number = #{idNumber}


		AND telephone = #{telephone}


		AND zone_id = #{zoneId}


		AND address = #{address}


		AND post_code = #{postCode}


		AND homePage = #{homePage}


		AND qq_number = #{qqNumber}


		AND aliPay = #{aliPay}


		AND state = #{state}

mapper.select:
    SELECT 
    	user_id			AS userId,
		real_name		AS realName,
		nick_name		AS nickName,
		gender			AS gender,
		age				AS age,
		id_number		AS idNumber,
		telephone       AS telephone,
		zone_id			AS zoneId,
		address			AS address,
		post_code		AS postCode,
		home_page		AS homePage,
		qq_number		AS qqNumber,
		ali_pay			AS aliPay,
		image			AS image,
		description		AS description,
	  	state			AS state
    FROM 
    	tbl_jinlong_system_user_info


			real_name = #{realName}


			AND nick_name = #{nickName}


			AND id_number = #{idNumber}


			AND telephone = #{telephone}


			AND zone_id = #{zoneId}


			AND address = #{address}


			AND post_code = #{postCode}


			AND homePage = #{homePage}


			AND qq_number = #{qqNumber}


			AND aliPay = #{aliPay}


	ORDER BY user_id

	SELECT 
		COUNT(1) 
    FROM 
    	tbl_jinlong_system_user_info


			real_name = #{realName}


			AND nick_name = #{nickName}


			AND id_number = #{idNumber}


			AND telephone = #{telephone}


			AND zone_id = #{zoneId}


			AND address = #{address}


			AND post_code = #{postCode}


			AND homePage = #{homePage}


			AND qq_number = #{qqNumber}


			AND aliPay = #{aliPay}

    SELECT 
    	user_id			AS userId,
		real_name		AS realName,
		nick_name		AS nickName,
		gender			AS gender,
		age				AS age,
		id_number		AS idNumber,
		telephone       AS telephone,
		zone_id			AS zoneId,
		address			AS address,
		post_code		AS postCode,
		home_page		AS homePage,
		qq_number		AS qqNumber,
		ali_pay			AS aliPay,
		image			AS image,
		description		AS description,
	  	state			AS state
    FROM 
    	tbl_jinlong_system_user_info
	ORDER BY user_id DESC


			real_name = #{realName}


			AND nick_name = #{nickName}


			AND id_number = #{idNumber}


			AND telephone = #{telephone}


			AND zone_id = #{zoneId}


			AND address = #{address}


			AND post_code = #{postCode}


			AND homePage = #{homePage}


			AND qq_number = #{qqNumber}


			AND aliPay = #{aliPay}



    	LIMIT #{startRow} , #{pageSize}
